import { useState } from "react";
import { useNavigate } from "react-router-dom";

function FormPage() {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    amount: ""
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://localhost:3001/send-confirmation", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
  // ✅ Confirmation email sent successfully
  // ✅ Build dynamic Venmo URL
const { amount } = formData;
const venmoURL = `https://venmo.com/@Todd-Walpole?txn=pay&amount=${encodeURIComponent(
  amount
)}&note=${encodeURIComponent("Split-O-Pot-omus")}`;

// Redirect user to Venmo
window.location.href = venmoURL;

  // ⏳ Optional: return to confirmation page after ~8 seconds
  setTimeout(() => navigate("/confirmation"), 8000);
}

    } catch (err) {
      console.error(err);
      alert("Server error.");
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "40px" }}>
      <h1>Split-O-Pot-omus Entry</h1>
      <form
        onSubmit={handleSubmit}
        style={{ display: "inline-block", textAlign: "left" }}
      >
        <div>
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </div>

        <div>
          <label>Email:</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>

        <div>
          <label>Amount ($):</label>
          <input
            type="number"
            name="amount"
            value={formData.amount}
            onChange={handleChange}
            required
          />
        </div>

        <button type="submit" style={{ marginTop: "10px" }}>
          Submit & Pay via Venmo
        </button>
      </form>
    </div>
  );
}

export default FormPage;
