import { useState } from "react";
import { useNavigate } from "react-router-dom";

function FormPage() {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    amount: ""
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
  e.preventDefault();
  console.log("Submit button clicked, sending request...");

  try {
    const response = await fetch("http://localhost:3001/send-confirmation", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(formData),
    });

    console.log("Backend response:", response.status);

    if (response.ok) {
      console.log("Email sent successfully, redirecting to Venmo...");

      const { amount } = formData;
      const venmoURL = `https://venmo.com/u/Todd-Walpole?txn=pay&amount=${encodeURIComponent(
        amount
      )}&note=${encodeURIComponent("Split-O-Pot-omus")}`;

      // Redirect to Venmo
      window.location.href = venmoURL;
setTimeout(() => {
  if (!/Android|iPhone|iPad/i.test(navigator.userAgent)) {
    alert("If Venmo didn't open automatically, click OK to open manually.");
    window.open(venmoURL, "_blank");
  }
}, 3000);

      // After 8 seconds, return to confirmation page
      setTimeout(() => navigate("/confirmation"), 8000);
    } else {
      alert("❌ Error sending confirmation email.");
    }
  } catch (err) {
    console.error("Server error:", err);
    alert("Server error.");
  }
};

  return (
    <div style={{ textAlign: "center", marginTop: "40px" }}>
      <h1>Split-O-Pot-omus Entry</h1>
      <form
        onSubmit={handleSubmit}
        style={{ display: "inline-block", textAlign: "left" }}
      >
        <div>
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </div>

        <div>
          <label>Email:</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>

        <div>
          <label>Amount ($):</label>
          <input
            type="number"
            name="amount"
            value={formData.amount}
            onChange={handleChange}
            required
          />
        </div>

        <button type="submit" style={{ marginTop: "10px" }}>
          Submit & Pay via Venmo
        </button>
      </form>
    </div>
  );
}

export default FormPage;
